import React, { useEffect, useState } from 'react';
import { CanvasJSChart } from 'canvasjs-react-charts';
import axios from 'axios'; 

const Line = ({ symbol }) => {

  const [price, setPrice] = useState([]);
  /* const [date, setDate] = useState([]); */
  const [stockInfo, setStockInfo] = useState([]);
  const [articles, setArticles] = useState([]);
  const [stock, setStock]= useState(symbol);

  function change(event) {
    setStock(event.target.value);
    getchartInfo();
  }

  useEffect(() => {
  const getchartInfo = async () => {
    const priceAndDate = await axios.get (
      'https://api.marketstack.com/v1/eod?access_key=7ba49202483340bca37ab953c66b592c&symbols=' + symbol , { mode: "no-cors" }
    );
    setPrice(priceAndDate.data.data);

    /* const prices = response.close
    console.log(prices);*/
    console.log(priceAndDate.data);
    
    /* console.log(date); */
    };
    getchartInfo();
  },
  [null]);

  const getchartInfo = async () => {
    const priceAndDate = await axios.get (
      'https://api.marketstack.com/v1/eod?access_key=7ba49202483340bca37ab953c66b592c&symbols=' + stock , { mode: "no-cors" }
    );
    setPrice(priceAndDate.data.data);
  }

  return ( 
      <div>
          <CanvasJSChart id='line'
            options = { {
              animationEnabled: true,
              axisY: {
                title: "USD",
                prefix: "$"
              },
              data: [{
                type: "line",
                yValueFormatString: "$#,###.##",
                dataPoints : price.map(price => ({
                  x: new Date(price.date),
                  y: Number(price.close)
                }))
              }],
              
            } }
          />

        <p onChange={change}>{symbol}</p>
      </div> 
    );
};

export default Line;
