import React from 'react';
import Whirligig from 'react-whirligig'
import Slide from '../components/News-Carousel/News_Home'; 
/* import Carousel from "../components/News-Carousel/News_Home"; */
import SimpleImageSlider from "react-simple-image-slider";
import placeholder from './placeholder.png';

const images = [
  { url: {background2} },
  { url: {background2} },
  { url: {background2} },
];
  
const Home = () => {
  let whirligig
  const next = () => whirligig.next()
  const prev = () => whirligig.prev()

  return (
    <div id='content'>
      <div id='title'>
        <h1>Top News Articles</h1>
      </div>
     
      <div id='imageSlider'>
        <button id="sliderbutton" onClick={prev}>Prev</button>
        <Whirligig
          visibleSlides={3}
          gutter="1em"
          ref={(_whirligigInstance) => { whirligig = _whirligigInstance}}
          >
            <img id="images" src={placeholder} />
            <img id="images" src={placeholder} />
            <img id="images" src={placeholder} />
            <img id="images" src={placeholder} />
            <img id="images" src={placeholder} />
        </Whirligig>
        <button id="sliderbutton" onClick={next}>Next</button>
      </div>
    
    </div>
  );
};
  
export default Home;

