import React from 'react';
/* import Slide from '../components/News-Carousel/News_Home'; */

import { Slide } from 'react-slideshow-image';
import background2 from './nkyv2xbkkbl51.png';

const slideImages = [
  './nkyv2xbkkbl51.png',
  './nkyv2xbkkbl51.png',
  './nkyv2xbkkbl51.png'
];
  
const Home = () => {
  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'left',
        height: '100vh',
        backgroundColor: '#9398b5'
      }}
    >
      <h1>Top News Articles</h1>

      <div>
          <Slide easing="ease">
            <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[0]})`}}>
              <span>Slide 1</span>
            </div>
            </div>
            <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
              <span>Slide 2</span>
            </div>
            </div>
            <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[2]})`}}>
              <span>Slide 3</span>
            </div>
            </div>
          </Slide>
      </div>
    </div>
  );
};
  
export default Home;
